        static int[] karpos = [61, 13]; //karakter jelenlegi helye

        static void Main(string[] args)
        {
            DrawMap(map, karpos);

            //Game loop
            while (true)
            {
                ConsoleKeyInfo key = Console.ReadKey(true);

                switch (key.Key)
                {
                    case ConsoleKey.A: karpos[0] -= 1; break;
                    case ConsoleKey.W: karpos[1] -= 1; break;
                    case ConsoleKey.D: karpos[0] += 1; break;
                    case ConsoleKey.S: karpos[1] += 1; break;
                    default: break;
                }
                DrawMap(map, karpos);
            }
        }

        static void DrawMap(string map, int[] pos)
        {
            Console.Clear();
            foreach (string line in map.Split("\n"))
            {
                foreach (char kar in line)
                {
                    switch (kar)
                    {
                        case '█': Console.BackgroundColor = ConsoleColor.White; Console.Write(" "); break;
                        case '▓': Console.BackgroundColor = ConsoleColor.DarkBlue; Console.ForegroundColor = ConsoleColor.Cyan; Console.Write("░"); break;
                        case '▒': Console.BackgroundColor = ConsoleColor.DarkGray; Console.Write(" "); break;
                        case '░': Console.BackgroundColor = ConsoleColor.DarkGreen; Console.ForegroundColor = ConsoleColor.Yellow; Console.Write("░"); break;
                        default: Console.BackgroundColor = ConsoleColor.Black; Console.Write("\n"); break;
                    }
                }
            }

            //Karakter rajzolás
            Console.SetCursorPosition(pos[0], pos[1]);
            Console.BackgroundColor = ConsoleColor.DarkGreen; 
            Console.ForegroundColor = ConsoleColor.Yellow; 
            Console.Write("@");

            Console.SetCursorPosition(119, 29);
        }
    }
}
